import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

// üö® Î∞±ÏóîÎìú APIÏùò Í∏∞Î≥∏ URL
// (CORS Ïò§Î•ò Î∞©ÏßÄÎ•º ÏúÑÌï¥ Î∞±ÏóîÎìú SecurityConfig/ControllerÏóê @CrossOrigin ÏÑ§Ï†ïÏù¥ ÌïÑÏöîÌï©ÎãàÎã§)
const API_BASE_URL = "http://localhost:8090";

function Notice({ currentUser }) {
    const [selectedNotice, setSelectedNotice] = useState(null);
    const [notices, setNotices] = useState([]);
    const [loading, setLoading] = useState(false);
    const [isEditing, setIsEditing] = useState(false);
    const [editedNotice, setEditedNotice] = useState(null);
    const [isSaving, setIsSaving] = useState(false); // Ï†ÄÏû•/ÏÇ≠Ï†ú Î°úÎî© ÏÉÅÌÉú (Ïú†ÏßÄ)

    // 1. fetchNotices (useCallback Ïú†ÏßÄ)
    const fetchNotices = useCallback(async () => {
        setLoading(true);
        try {
            // Î∞±ÏóîÎìú URLÎ°ú ÏàòÏ†ï
            const response = await axios.get(`${API_BASE_URL}/notice/list/1`);
            const sortedNotices = response.data.noticeList.sort((a, b) => b.noticeId - a.noticeId);
            setNotices(sortedNotices);
        } catch (error) {
            console.error("Í≥µÏßÄÏÇ¨Ìï≠ÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§:", error);
            if (error.response && error.response.status === 403) {
                 alert("Í≥µÏßÄÏÇ¨Ìï≠ Î™©Î°ùÏùÑ Î∂àÎü¨Ïò¨ Í∂åÌïúÏù¥ ÏóÜÏäµÎãàÎã§. (403 Forbidden)");
            } else {
                 alert("Í≥µÏßÄÏÇ¨Ìï≠ÏùÑ Î∂àÎü¨Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
            }
        } finally {
            setLoading(false);
        }
    }, []); 

    // 2. useEffect (useCallback ÏùòÏ°¥ÏÑ± Ïú†ÏßÄ)
    useEffect(() => {
        fetchNotices();
    }, [fetchNotices]); 

    // 3. isAdmin (Ïú†ÏßÄ)
    const isAdmin = currentUser && currentUser.role === 'A';

    // 4. handleCreate, handleEdit (Ïú†ÏßÄ)
    const handleCreate = () => {
        setEditedNotice({ title: '', cont: '' });
        setIsEditing(true);
        setSelectedNotice(null);
    };

    const handleEdit = (notice) => {
        setEditedNotice({ ...notice });
        setIsEditing(true);
        setSelectedNotice(null);
    };

    // 5. handleDelete (Ïú†ÏßÄ - URLÏù¥ Î∞±ÏóîÎìúÏôÄ ÏùºÏπòÌï®)
    const handleDelete = async (noticeId) => {
        if (window.confirm("Ï†ïÎßêÎ°ú Ïù¥ Í≥µÏßÄÏÇ¨Ìï≠ÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) {
            setIsSaving(true); 
            try {
                // Î∞±ÏóîÎìú URLÎ°ú ÏàòÏ†ï
                await axios.delete(`${API_BASE_URL}/notice/delete/${noticeId}`);
                
                alert("Í≥µÏßÄÏÇ¨Ìï≠Ïù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.");
                setSelectedNotice(null);
                await fetchNotices(); // Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
            } catch (error) {
                console.error("Í≥µÏßÄÏÇ¨Ìï≠ ÏÇ≠Ï†ú Ï§ë Ïò§Î•ò:", error);
                if (error.response && error.response.status === 403) {
                    alert("ÏÇ≠Ï†ú Í∂åÌïúÏù¥ ÏóÜÏäµÎãàÎã§. (403 Forbidden)\nSecurityConfigÏóêÏÑú /notice/delete/** Î•º permitAll ÌñàÎäîÏßÄ, ÏÑúÎ≤ÑÎ•º Ïû¨ÏãúÏûëÌñàÎäîÏßÄ ÌôïÏù∏ÌïòÏÑ∏Ïöî.");
                } else {
                    alert("ÏÇ≠Ï†ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
                }
            } finally {
                setIsSaving(false); 
            }
        }
    };

    // 6. üö® handleSave (Î∞±ÏóîÎìúÏóê ÎßûÍ≤å ÎåÄÌè≠ ÏàòÏ†ï)
    const handleSave = async (e) => {
        e.preventDefault();
        setIsSaving(true);

        const isNew = !editedNotice.noticeId;

        // [ÏàòÏ†ï 1] Î∞±ÏóîÎìú NoticeControllerÎäî JSONÏù¥ ÏïÑÎãå FormDataÎ•º Î∞õÏäµÎãàÎã§.
        const formData = new FormData();
        formData.append('title', editedNotice.title);
        formData.append('cont', editedNotice.cont);

        // [ÏàòÏ†ï 2] DBÏùò USER_ID (NOT NULL) Ïª¨ÎüºÏùÑ Ï±ÑÏö∞Í∏∞ ÏúÑÌï¥ userId Ï†ÑÏÜ°
        // (currentUser propÏóê 'userId' ÌïÑÎìúÍ∞Ä ÏûàÎã§Í≥† Í∞ÄÏ†ïÌï©ÎãàÎã§. 'id' Îì± Îã§Î•∏ ÌïÑÎìúÎ™ÖÏùº Ïàò ÏûàÏäµÎãàÎã§.)
        if (!currentUser || !currentUser.userId) {
            console.error("currentUser.userIdÍ∞Ä ÏóÜÏäµÎãàÎã§!", currentUser);
            alert("Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥(userId)Í∞Ä ÏóÜÏñ¥ Ï†ÄÏû•Ìï† Ïàò ÏóÜÏäµÎãàÎã§.");
            setIsSaving(false);
            return;
        }
        formData.append('userId', currentUser.userId);

        try {
            if (isNew) {
                // [ÏàòÏ†ï 3] ÏÉà Í∏Ä ÏûëÏÑ± URLÏùÄ '/notice/add'Í∞Ä ÏïÑÎãå '/notice/write' ÏûÖÎãàÎã§.
                await axios.post(`${API_BASE_URL}/notice/write`, formData, {
                    headers: { 'Content-Type': 'multipart/form-data' }
                });
                alert('Í≥µÏßÄÏÇ¨Ìï≠Ïù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏûëÏÑ±ÎêòÏóàÏäµÎãàÎã§.');
            } else {
                // [ÏàòÏ†ï 4] ÏàòÏ†ï ÏãúÏóêÎèÑ FormDataÎ•º ÏÇ¨Ïö©ÌïòÍ≥†, noticeIdÎ•º Ìè¨Ìï®ÏãúÌÇµÎãàÎã§.
                formData.append('noticeId', editedNotice.noticeId);

                // [ÏàòÏ†ï 5] ÏàòÏ†ï URLÏùÄ '/notice/update' ÏûÖÎãàÎã§. (JSON ÎåÄÏã† FormData Ï†ÑÏÜ°)
                await axios.put(`${API_BASE_URL}/notice/update`, formData, {
                    headers: { 'Content-Type': 'multipart/form-data' }
                });
                alert('Í≥µÏßÄÏÇ¨Ìï≠Ïù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§.');
            }

            // ÏÑ±Í≥µ Ïãú Í≥µÌÜµ ÏûëÏóÖ (Ïú†ÏßÄ)
            setIsEditing(false);
            setEditedNotice(null);
            setSelectedNotice(null);
            await fetchNotices(); // Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®

        } catch (error) {
            console.error("Í≥µÏßÄÏÇ¨Ìï≠ Ï†ÄÏû• Ï§ë Ïò§Î•ò:", error);
            if (error.response && error.response.status === 403) {
                alert("Ï†ÄÏû• Í∂åÌïúÏù¥ ÏóÜÏäµÎãàÎã§. (403 Forbidden)\nSecurityConfigÏóêÏÑú /notice/write, /notice/updateÎ•º permitAll ÌñàÎäîÏßÄ, ÏÑúÎ≤ÑÎ•º Ïû¨ÏãúÏûëÌñàÎäîÏßÄ ÌôïÏù∏ÌïòÏÑ∏Ïöî.");
            } else if (error.response && error.response.status === 500) {
                 alert("ÏÑúÎ≤Ñ ÎÇ¥Î∂Ä Ïò§Î•ò (500).\nÏΩòÏÜî Î°úÍ∑∏Î•º ÌôïÏù∏ÌïòÏÑ∏Ïöî. (USER_IDÍ∞Ä nullÏù¥ ÏïÑÎãåÏßÄ, ÏãúÌÄÄÏä§Í∞Ä ÏûàÎäîÏßÄ ÌôïÏù∏)");
            } else {
                alert("Ï†ÄÏû• Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
            }
        } finally {
            setIsSaving(false); 
        }
    };

    // --- (Ïù¥Ìïò Î†åÎçîÎßÅ ÏΩîÎìúÎäî ÎèôÏùº) ---

    if (loading) {
        return <div className="text-center">Î°úÎî© Ï§ë...</div>;
    }

    return (
        <div className="w-full max-w-4xl mx-auto">
            <div className="flex justify-between items-center mb-6">
                <h1 className="text-3xl font-bold">Í≥µÏßÄÏÇ¨Ìï≠</h1>
                {isAdmin && !isEditing && (
                    <button onClick={handleCreate} className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                        Í∏ÄÏì∞Í∏∞
                    </button>
                )}
            </div>

            {isEditing ? (
                <form onSubmit={handleSave} className="bg-gray-800/50 p-6 sm:p-8 rounded-lg">
                    <input 
                        type="text" 
                        value={editedNotice.title}
                        onChange={(e) => setEditedNotice({ ...editedNotice, title: e.target.value })}
                        placeholder="Ï†úÎ™©" 
                        className="w-full bg-gray-700 text-white p-2 rounded mb-4" 
                        required 
                        disabled={isSaving} // (Ïú†ÏßÄ)
                    />
                    <textarea 
                        value={editedNotice.cont}
                        onChange={(e) => setEditedNotice({ ...editedNotice, cont: e.target.value })}
                        placeholder="ÎÇ¥Ïö©" 
                        className="w-full bg-gray-700 text-white p-2 rounded mb-4 h-48" 
                        required 
                        disabled={isSaving} // (Ïú†ÏßÄ)
                    />
                    <div className="flex justify-end gap-4">
                        <button type="button" onClick={() => setIsEditing(false)}
                            className="bg-gray-600 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded disabled:opacity-50" // (Ïú†ÏßÄ)
                            disabled={isSaving}> 
                            Ï∑®ÏÜå
                        </button>
                        <button type="submit"
                            className="bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded disabled:opacity-50" // (Ïú†ÏßÄ)
                            disabled={isSaving}>
                            {isSaving ? 'Ï†ÄÏû• Ï§ë...' : 'Ï†ÄÏû•'} 
                        </button>
                    </div>
                </form>
            ) : selectedNotice ? (
                <div className="bg-gray-800/50 p-6 sm:p-8 rounded-lg">
                    <h2 className="text-2xl font-bold mb-2">{selectedNotice.title}</h2>
                    <p className="text-gray-400 text-sm mb-6 border-b border-gray-700 pb-4">
                        ÏûëÏÑ±Ïùº: {new Date(selectedNotice.createdAt).toLocaleDateString()}
                    </p>
                    <div className="text-gray-300 whitespace-pre-wrap">
                        {selectedNotice.cont}
                    </div>
                    <div className="flex justify-between items-center mt-8">
                        <button onClick={() => setSelectedNotice(null)}
                            className="bg-gray-600 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded">
                            Î™©Î°ùÏúºÎ°ú
                        </button>
                        {isAdmin && (
                            <div className="flex gap-4">
                                <button onClick={() => handleEdit(selectedNotice)}
                                    className="bg-yellow-600 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded">
                                    ÏàòÏ†ï
                                </button>
                                <button onClick={() => handleDelete(selectedNotice.noticeId)}
                                    className="bg-red-600 hover:bg-red-700 text-white font-bold py-2 px-4 rounded disabled:opacity-50"
                                    disabled={isSaving}> 
                                    {isSaving ? 'ÏÇ≠Ï†ú Ï§ë...' : 'ÏÇ≠Ï†ú'}
                                </button>
                            </div>
                        )}
                    </div>
                </div>
            ) : (
                <div className="bg-gray-800/50 rounded-lg overflow-hidden">
                    <table className="w-full text-sm text-left text-gray-300">
                        <thead className="text-xs text-gray-400 uppercase bg-gray-700/50">
                            <tr>
                                <th scope="col" className="px-6 py-3 w-1/6">Î≤àÌò∏</th>
                                <th scope="col" className="px-6 py-3 w-3/6">Ï†úÎ™©</th>
                                <th scope="col" className="px-6 py-3 w-2/6">ÏûëÏÑ±Ïùº</th>
                            </tr>
                        </thead>
                        <tbody>
                            {notices.map(notice => (
                                <tr key={notice.noticeId} className="border-b border-gray-700 hover:bg-gray-700/50">
                                    <td className="px-6 py-4">{notice.noticeId}</td>
                                    <td className="px-6 py-4">
                                        <button onClick={() => setSelectedNotice(notice)} className="font-medium hover:underline text-left">
                                            {notice.title}
                                        </button>
                                    </td>
                                    <td className="px-6 py-4">{new Date(notice.createdAt).toLocaleDateString()}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
}

export default Notice;
