import React, { useEffect, useRef } from "react";
import instance from "../../token/interceptors";

const Logout = ({ onNavigate, onLogout }) => {
  const hasRun = useRef(false);

  const config = {
    headers: {
      "Content-Type": "application/json",
      Authorization: localStorage.getItem("Authorization"),
      "Authorization-refresh": localStorage.getItem("Authorization-refresh"),
    },
  };

  const handleCommit = async () => {
    console.log("üßπ logout start");
    const userId = localStorage.getItem("userId");
    console.log("logout userId =>", userId);

    try {
      await instance.delete(`/member/logout`, config);
    } catch (error) {
      console.warn("logout Ïã§Ìå® =>", error.message);
    }

    // ‚úÖ Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄ ÏôÑÏ†Ñ Ï†ïÎ¶¨
    localStorage.clear();

    // ‚úÖ App ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî
    onLogout(); // currentUser = null, currentPage = main
    onNavigate("main"); // Î©îÏù∏ÌôîÎ©¥ÏúºÎ°ú Ïù¥Îèô

    console.log("‚úÖ logout ÏôÑÎ£å, Î©îÏù∏ ÌéòÏù¥ÏßÄ Ïù¥Îèô");
  };

  useEffect(() => {
    if (!hasRun.current) {
      hasRun.current = true;
      handleCommit();
    }
  }, []);

  return null; // Î≥ÑÎèÑ Î†åÎçîÎßÅ ÌïÑÏöî ÏóÜÏùå
};

export default Logout;
