import React, { useState, useEffect } from 'react';

function Notice() {
    // 1. 'selectedNotice' 상태 추가: 사용자가 클릭한 공지사항을 저장합니다.
    // 이 값이 null이면 목록을, 객체이면 상세 내용을 보여줍니다.
    const [selectedNotice, setSelectedNotice] = useState(null);
    const [notices, setNotices] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // 2. DB 스키마에 맞춘 더미 데이터 (향후 fetch로 교체될 부분)
        const dummyNotices = [
            { 
                noticeNo: 3, 
                noticeTitle: "✨ Gemini AI 소견 요약 기능 추가 안내", 
                noticeContent: "AI 분석 후, Gemini API를 통해 생성된 전문가 수준의 소견 요약 리포트를 확인하실 수 있습니다. \n\n이제 더욱 상세하고 정확한 정보를 바탕으로 진단에 참고하세요.", 
                createDate: "2025-10-03" 
            },
            { 
                noticeNo: 2, 
                noticeTitle: "정식 서비스 오픈 안내", 
                noticeContent: "LungMate AI 정식 서비스를 시작합니다. 많은 이용 부탁드립니다.", 
                createDate: "2025-10-02" 
            },
            { 
                noticeNo: 1, 
                noticeTitle: "시스템 점검 안내 (완료)", 
                noticeContent: "보다 안정적인 서비스 제공을 위해 시스템 점검을 진행했습니다.", 
                createDate: "2025-10-01" 
            },
        ];
        setNotices(dummyNotices);
        setLoading(false);
    }, []);

    if (loading) {
        return <div className="text-center">로딩 중...</div>;
    }

    // 3. 조건부 렌더링: 선택된 공지사항이 있으면 상세 보기, 없으면 목록 표시
    return (
        <div className="w-full max-w-4xl mx-auto">
            <h1 className="text-3xl font-bold mb-6">공지사항</h1>
            
            {selectedNotice ? (
                // --- 상세 보기 UI ---
                <div className="bg-gray-800/50 p-6 sm:p-8 rounded-lg glassmorphism">
                    <h2 className="text-2xl font-bold mb-2">{selectedNotice.noticeTitle}</h2>
                    <p className="text-gray-400 text-sm mb-6 border-b border-gray-700 pb-4">
                        작성일: {selectedNotice.createDate}
                    </p>
                    <div className="text-gray-300 whitespace-pre-wrap">
                        {selectedNotice.noticeContent}
                    </div>
                    <button 
                        onClick={() => setSelectedNotice(null)} // '목록으로' 버튼 클릭 시 상태를 null로 초기화
                        className="mt-8 bg-gray-600 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded"
                    >
                        목록으로
                    </button>
                </div>
            ) : (
                // --- 목록 보기 UI (테이블) ---
                <div className="bg-gray-800/50 rounded-lg glassmorphism overflow-hidden">
                    <table className="w-full text-sm text-left text-gray-300">
                        <thead className="text-xs text-gray-400 uppercase bg-gray-700/50">
                            <tr>
                                <th scope="col" className="px-6 py-3 w-1/6">번호</th>
                                <th scope="col" className="px-6 py-3 w-3/6">제목</th>
                                <th scope="col" className="px-6 py-3 w-2/6">작성일</th>
                            </tr>
                        </thead>
                        <tbody>
                            {notices.map(notice => (
                                <tr key={notice.noticeNo} className="border-b border-gray-700 hover:bg-gray-700/50">
                                    <td className="px-6 py-4">{notice.noticeNo}</td>
                                    <td className="px-6 py-4">
                                        <button 
                                            onClick={() => setSelectedNotice(notice)} // 제목 클릭 시 setSelectedNotice 함수 호출
                                            className="font-medium hover:underline text-left"
                                        >
                                            {notice.noticeTitle}
                                        </button>
                                    </td>
                                    <td className="px-6 py-4">{notice.createDate}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
}

export default Notice;
